public function getProductTags2($product_id) {
	$query = $this->db->query("SELECT tag FROM " . DB_PREFIX . "product_description WHERE product_id = '" . (int)$product_id . "' AND language_id = '" . (int)$this->config->get('config_language_id') . "'");

	return $query->rows;
}
